  w7_ab_accuracy_rf <- append(w7_ab_accuracy_rf,mean(fit.rf$resample$Accuracy))
  
  
  
  # Section 6. Make Predictions
  
  # need to convert SUCCESS to factor...
  test_var <- as.factor(validation$SUCCESS)
  validation$SUCCESS <- test_var
  
